		for(var c = 0;c <= instance.totFitasRabiola;c++) geometry.vertices[c] = new THREE.Vector3(0, 0, 0);//criando Vector3 q será atualizado - obrigatório
		instance.lineRabi = new THREE.Line( geometry, new THREE.LineBasicMaterial( { color:'#FFF', transparent:true, depthWrite:false, opacity:0.4 } ) );
		instance.lineRabi.frustumCulled = false;//evita linha sumir
		setAutoUpdateMatrix(instance.lineRabi, false);
		scene.add( instance.lineRabi );

