		if(usageAvatar){
			instance.curve.v0.setFromMatrixPosition(instance.getPerso.indicadorMaoDireita.matrixWorld);
		}else{
			//instance.curve.v0.set(x1 + 5, 10, z1+5);
			instance.curve.v0.set(x1 + 5, y1+12, z1-5);
		}
		

