	this.animaLadoLado = function(){

		
		instance.posZ = instance.posZ + (0 - instance.posZ) / 30;//volta rotação da pipa p/ o centro
		
		//Volta com a pipa p/ o centro do perso - somente se rotação estiver ok
		if(Math.abs(instance.posZ) < 1){
			
			instance.zigzag(instance.velLadoLado);
			instance.pipa.translateX( instance.velocidade * -0.3 );
			
			if(Math.abs(instance.pipa.position.x - instance.perso.position.x) > 10){
				if(instance.pipa.position.x > instance.perso.position.x) instance.pipa.translateX( -0.3 );
				else if(instance.pipa.position.x < instance.perso.position.x) instance.pipa.translateX( 0.3 );
			}

			//centraliza posição Y da pipa
			var limiteY = instance.distance * 0.6;
			if(instance.descarregar == 0){
				instance.pipa.position.y = instance.pipa.position.y + (limiteY - instance.pipa.position.y) / instance.distance;//300;//1220 até 10
				instance.pipa.position.z = instance.pipa.position.z + (instance.pipa.position.distanceTo( instance.perso.position ) - instance.distance);
			}

		}

	}

	this.zigzag = function(velLadoLado){

		//velLadoLado = 0.2;

		instance.limiteVelocidade.right = instance.limiteVelocidade.right + (instance.limiteVelocidade.limite - instance.limiteVelocidade.right) * 0.1;
		instance.acres = instance.acres + ((instance.directionRight * velLadoLado) - instance.acres) * velLadoLado;
		instance.velocidade += instance.acres;

		if(instance.velocidade > instance.limiteVelocidade.right) {
			instance.velocidade = instance.limiteVelocidade.right;
			instance.directionRight = -1;
		}else if(instance.velocidade < -(instance.limiteVelocidade.right)){
			instance.velocidade = -(instance.limiteVelocidade.right);
			instance.directionRight = 1;
		}
		
		instance.posZ = instance.posZ - (instance.velocidade * 0.009);

	}

