				__y = rabi.position.y - (1 - newPosZ),
				__z = rabi.position.z - 0.75;
				
				/*divDelay = ((instance.puxar > 0.05) ? 4.5 : 10);
				if(instance.aparou) {
					divDelay = 15;
				}else if(instance.morreu){//se não tiver no aparo e tiver morrido
					__z = rabi.position.z + 0.5;
					divDelay = 7;
				}*/

				




				__x = __x + (instance.rabX - __x) / divDelay;
				__y = __y + (instance.rabY - __y) / divDelay;
				//if(instance.descarregar != 0) divDelay = 2;
				__z = __z + (instance.rabZ - __z) / divDelay;


				/*if(instance.rodar){
					if(instance.descarregar != 0 && c < 10) __z = instance.rabZ - 5;//evita rabiola passar na frente da pipa
				}*/

				
