	this.setFatiar = function(idc){
		var obj = instance.pipasAparadas[idc];
		if(obj != undefined){
			obj.setMorreu();
			obj.podeAparar = false;
			setTimeout(function(){//PODE APARAR DENOVO DEPOIS DE 1.4seg
				if(instance != undefined && instance.pipa != undefined) obj.podeAparar = true;
			}, 1400);
			instance.pipasAparadas.splice(idc, 1);
			if(instance.pipaPrincipal) playSom('facao', 1);
		}
	}

