	this.removeCabresto = function(){
		//removendo cabresto
		var t = instance.pipa.children.length;
		if(t > 0){//if(t > 1){//era 1 quando tinha dois lados a pipa
			while(t--){
				removeScene(instance.pipa.children[t], instance.pipa);
				//if(t == 1) break;//necessario se pipa tiver os dois lados
			}
		}
	}

	this.setEffPipaCaindo = function(obj){

		instance.animatedLadoAlado = false;
		clearTweenPipa(instance);
		clearTimeout(instance.timeout);

		ajustBugRotacaoPosZ(instance);
		ajustBugRotacaoPipaZ(instance.pipa);
		
		
		



		TweenMax.to(instance, 1.8, { 
	    	overwrite:1,
	    	ease:Cubic.easeOut,
	        //posZ: ((instance.posZ < 0) ? -1 * _180graus : _180graus),
	        //puxar: 0.85
	        puxar: 0.7
	    });
	    
	    //console.log(instance.pipa.rotation);

	    TweenMax.to(instance.pipa.rotation, 1.4-(instance.puxar/6), { 
	    	//x:0,
	    	//y:0,
			z:((instance.posZ < 0) ? _180graus : -1 * _180graus)
		});

		
		//NEW
		var tot = 40;//parseInt(instance.pipa.position.y / 10),
		arrBezier = [];
		while(tot--){
			arrBezier.push( { x:rand(de2ra(10), de2ra(90)), y:rand(de2ra(-50), de2ra(50)) } );
		}
		
		TweenMax.to(instance.pipa.rotation, 60, { delay:1, bezier:arrBezier, ease:Linear.easeNone });



	}

