	this.removeLine = function(){
		if(instance.line != undefined){
			removeScene(instance.line);
			removeScene(instance.line2);
			removeScene(instance.line3);
			instance.line = instance.line2 = instance.line3 = undefined;
		}
	}

	this.matarPipa = function(){

		if(instance == undefined) return;

		instance.matarPipasAparo();

		clearTweenPipa(instance);
		clearTimeout(instance.timeout);

		instance.morreu = true;

		removeScene(instance.pipa1, instance.pipa);
		
		

		for(var c = 0;c < instance.totFitasRabiola;c++) removeScene(instance.rabiola[c]);
		//for(var c = 0;c < instance.arrCabresto.length;c++) removeScene(instance.arrCabresto[c], instance.pipa);
		instance.removeCabresto();
		
		instance.removeLine();
		

		removeScene(instance.pipa/*, instance.pipa2*/);

		if(instance.carretilha != undefined){
			removeScene(instance.carretilha, instance.getPerso.avatar);
			instance.carretilha = undefined;
		}

		removeScene(instance.pipa);
		removeScene(instance.lineRabi);

		

		//remove do Array pipas essa pipa q não deve mais existir
		var tot2 = pipas.length;
		while(tot2--) {
			if(pipas[tot2] == instance){
				pipas.splice(tot2, 1);
			}
		}
		
		if(instance.getPerso != undefined && instance.getPerso.paiPipa == instance) instance.getPerso.paiPipa = undefined;//evita update da pipa q não está no alto

		/*instance.geometry.dispose();
		instance.geometry = undefined;
		instance.pipa1 = undefined;
		instance.pipa2 = undefined;
		instance.getPerso = undefined;*/
		
		instance.perso = undefined;
		instance.pipa = undefined;
		instance.lineRabi = undefined;
		instance.getPerso = undefined;
		instance.carretilha = undefined;

		//console.log(instance);
		
		instance = undefined;
		
	}


